import Header from '../../components/HeaderBlock/index.js';
import Layout from '../../components/LayoutBlock/index.js';
import Footer from '../../components/FooterBlock/index.js';

import bg3 from '../../assets/bg3.jpeg';
import MenuHeader from '../../components/MenuHeader/';


function HomePage({onChangePage}) {

    const handleChangePage = (page) => {
        if(typeof onChangePage === "function") onChangePage(page);
    }

    return (
        <>
        <MenuHeader onChangePage={handleChangePage} />
            <Header title="Pokemon Game 1" descr="This is simple triple triad card game" onClickButton={handleChangePage} />


            <Layout id="about" title="Full Rules" urlBg={bg3} >
                <p>In the game two players face off against one another, one side playing as "blue", the other as "red" on a 3 x3 grid. </p>
                <p>Each player has five cards in a hand and the aim is to capture the opponent 's cards by turning them into the player' s own color of red or blue. </p>
                <p>To win, a majority of the total ten cards played(including the one card that is not placed on the board) must be of the player 's card color. To do this, the player must capture cards by placing a card adjacent to an opponent' s card whereupon the 'ranks' of the sides where the two cards touch will be compared.If the rank of the opponent 's card is higher than the player' s card, the player 's card will be captured and turned into the opponent' s color.If the player 's rank is higher, the opponent' s card will be captured and changed into the player 's color instead. </p>
            </Layout>
            <Footer />
        </>
    )
};

export default HomePage;
